mapper.update:
update scfp_chain


        chain_no = #{chain_no},


        network_id = #{network_id},


        bank = #{bank},


        fund_id = #{fund_id},


        money = #{money}


        surplus = #{surplus}


        file_id = #{file_id}


        deadline = #{deadline}


        status = #{status}


where id = #{id}

mapper.select:
select * from scfp_chain where 1=1

     and id = #{id}


     and chain_no = #{chain_no}


     and bank like '%${bank}%'


     and fund_id =#{fund_id}


     and money = #{money}


     and surplus = #{surplus}


     and deadline =#{deadline}


     and status = #{status}

order by id desc
